Bash("C:\\Users\\nathan.carvalho\\Documents\\conBank\\frontend\\nginx.conf" << 'NGINX'
server {
    listen 80;
    server_name _;
    root /usr/share/nginx/html;
    index index.html;

    # Compressão gzip
    gzip on;
    gzip_vary on;
    gzip_proxied any;
    gzip_types text/plain text/css application/json application/javascript
               text/xml application/xml text/javascript image/svg+xml;
    gzip_min_length 256;

    # Cache longo para assets com hash no nome \(Vite\)
    location ~* \\.\(js|css|woff2?|png|jpg|jpeg|gif|ico|svg|webp\)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
        access_log off;
    }

    # SPA fallback: todas as rotas caem no index.html
    location / {
        try_files $uri $uri/ /index.html;
        add_header X-Content-Type-Options "nosniff";
        add_header X-Frame-Options "SAMEORIGIN";
        add_header X-XSS-Protection "1; mode=block";
    }

    # Health do Nginx \(usado pelo EasyPanel healthcheck\)
    location /nginx-health {
        access_log off;
        return 200 "ok\\n";
        add_header Content-Type text/plain;
    }
}
NGINX)
